import React from 'react';
import Link from '@material-ui/core/Link';
import { createMuiTheme, makeStyles, ThemeProvider } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
// import Title from './Title';
import { useParams } from 'react-router';
import gql from 'graphql-tag';
import { useQuery } from '@apollo/client';
import AllGuests from './AllGuests';
import AllUsers from './AllUsers'
import Title from './Title';
import { InfoSharp } from '@material-ui/icons';
import {} from '@material-ui/icons'
import { Button, Icon, IconButton, Popover } from '@material-ui/core';

function preventDefault(event) {
  event.preventDefault();
}

const useStyles = makeStyles((theme) => ({
  depositContext: {
    flex: 1,
  },
  title:{
    color:"#607d8b"
  },
  typography: {
    padding: theme.spacing(2),
  },
  container:{
    display:"flex"
  }
}));

const SHOW_FILE = gql`
query findroom($id:String){
  findRoom(id:$id){
    roomID
    roomname
    creator
  }
}
`

export default function Deposits() {

  const classes = useStyles();
  const { room } = useParams()
  const [anchorEl, setAnchorEl] = React.useState(null);
  const{ loading,error,data} = useQuery(SHOW_FILE,{
    variables: {id:room},
    
  })
 
  if (loading ) return <p>{data}</p>

 
  

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const open = Boolean(anchorEl);
  const id = open ? 'simple-popover' : undefined;
  
  return (
    <React.Fragment>
      <div className={classes.container}>
      <div>
      <h3 className={classes.title}>
      {data.findRoom.roomname}
      </h3>
      </div>
     <div>
        <IconButton aria-describedby={id} variant="contained" color="primary" onClick={handleClick}>
        <Icon>
          <InfoSharp/>
        </Icon>
      </IconButton>
      
      <Popover
        id={id}
        open={open}
        anchorEl={anchorEl}
        onClose={handleClose}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'center',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'center',
        }}
      >
        <Typography className={classes.typography}>The content of the Popover.</Typography>
      </Popover>
      </div>
    
      <AllUsers/>
    </div>
    </React.Fragment>
  );
}
