import React from 'react';
import Link from '@material-ui/core/Link';
import { createMuiTheme, makeStyles, ThemeProvider } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
// import Title from './Title';
import { useParams } from 'react-router';
import gql from 'graphql-tag';
import { useQuery } from '@apollo/client';
import AllGuests from './AllGuests';
import AllUsers from './AllUsers'
import Title from './Title';
import { InfoSharp } from '@material-ui/icons';
import {} from '@material-ui/icons'
import { Button, Icon, IconButton, Popover } from '@material-ui/core';

function preventDefault(event) {
  event.preventDefault();
}

const useStyles = makeStyles((theme) => ({
  depositContext: {
    flex: 1,
  },
  title:{
    color:"#607d8b",
    flex:1
  },
  typography: {
    paddingLeft:10,
    paddingRight:10,
    
  },
  container:{
    display:"flex"
  },
  icon:{
    color:"#cfd8dc"
  },
  header:{
    color:"#607d8b"
  }
}));

const SHOW_FILE = gql`
query findroom($id:String){
  findRoom(id:$id){
    roomID
    roomname
    creator
  }
}
`

export default function Deposits() {

  const classes = useStyles();
  const { room } = useParams()
  const [anchorEl, setAnchorEl] = React.useState(null);
  const{ loading,error,data} = useQuery(SHOW_FILE,{
    variables: {id:room},
    
  })
 
  if (loading ) return <p>{data}</p>

 
  

  const handlePopoverOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handlePopoverClose = () => {
    setAnchorEl(null);
  };

  const open = Boolean(anchorEl);
  const id = open ? 'simple-popover' : undefined;
  
  return (
    <React.Fragment>
      <div className={classes.container}>

      <div className={classes.title}>
      <h3 className={classes.title}>
      {data.findRoom.roomname}
      </h3>
      </div>

     <div>
     <Typography
        aria-owns={open ? 'mouse-over-popover' : undefined}
        aria-haspopup="true"
        onMouseEnter={handlePopoverOpen}
        onMouseLeave={handlePopoverClose}
      >
           <Icon>
          <InfoSharp/>
        </Icon>
      
      </Typography>
     
      
      <Popover
       
       id="mouse-over-popover"
       className={classes.popover}
       classes={{
         paper: classes.paper,
       }}
       open={open}
       anchorEl={anchorEl}
       anchorOrigin={{
         vertical: 'bottom',
         horizontal: 'left',
       }}
       transformOrigin={{
         vertical: 'top',
         horizontal: 'left',
       }}
       onClose={handlePopoverClose}
       disableRestoreFocus
      >
        <Typography  className={classes.typography}>
          <p className={classes.header}>created by <AllGuests params={data.findRoom.creator}/></p>
          <p>on 15 jan 2021</p>
        </Typography>
      </Popover>
      </div>
    
      
    </div>
    <AllUsers/>
    </React.Fragment>
  );
}
