import {React,useState,useContext} from 'react'
import { userMutation, gql, useMutation } from '@apollo/client'
import { Redirect } from 'react-router'
import AuthContext from "../context/auth-context";


class Guest{
    // constructor(username,password,email){
    //     this.username = username
    //     this.password = password
    //     this.email = email
    // }


    handleUsername(e){
        console.log(e.target.value)
    }
 
    
}


export default function Userlogin(){


    const g = new Guest();
    const auth = useContext(AuthContext)
    const UPLOAD_USER = gql`
    mutation($username:String,$email:String,$password:String){
        addUser(username:$username,email:$email,password:$password){
          _id
          username
          email
          isGuest
        }
      }
`

const[createUser] = useMutation(UPLOAD_USER,{
    onCompleted: data => {
        console.log()
    }
})


let [state, setState] = useState({
    username:'',
    password:'',
    email:'',
    submitted:false
});

const handleUsername = (e)=>{
    // console.log(state)
    // setState({username:e.target.value,password:state.password,email:state.email})
}
const handlePassword = (e)=>{
    // console.log(state)
    // setState({username:state.username,password:e.target.value,email:state.email})
}
const handleEmail = (e)=>{
    // console.log(state)
    // setState({username:state.username,password:state.password,email:e.target.value})
}

const handleSubmit = (e)=>{
    e.preventDefault()
    createUser({ variables: { username:state.username,
        email:state.email,password:state.password } })
        .then(res=>{
            // console.log(res.data.addUser)
            if(res.data){
                // console.log(res.data.addUser._id)
                auth.login(res.data.addUser._id,res.data.addUser.username)
            }
        })
        setState({submitted:true})
    
    
}

    if(!state.submitted){
    return(
        <form onSubmit={handleSubmit}>
            <input type="text" placeholder="guestname" name="username" 
            onChange={g.handleUsername}/>
            <input type="password" placeholder="password" name="password" 
            onChange={handlePassword}/>
            <input type="text" placeholder="email" name="email" 
            onChange={handleEmail}/>
            <input type="submit"/>
        </form>
    )
    }
    else{
        return <Redirect to='/initJoinRoom'/>
    }
}
