import React from 'react'
import { useState } from 'react'

import {  gql, useQuery } from '@apollo/client'


const SHOW_FILE = gql`
  query file ($roomid:String){
      files(roomid:$roomid){
        _id
        speaker
        file
      }
  }
`
const FIND = gql`
    query find($userid:String){
      finduser(id:$userid){
        _id
        username
        email
      }
    }
`
function Find(arg){
  const{ loading,error,data} = useQuery(FIND,{
    variables: {userid:arg}
  })

  return data
}

export default function FS(){
    const{ loading,error,data} = useQuery(SHOW_FILE,{
      variables: {roomid:"a887d8ae-3c6b-413f-a7fc-0ae0c8311dbe"}
    })
   
    
   
    if (loading) return null

     
    let audio = []
    // console.log(data.files)
    data.files.map((voice)=>{
        let user=Find(voice.speaker)
        audio.push(
            <audio key={voice._id} controls>
              <h3>{user.username}</h3>
          <source  src={`http://localhost:4000/Audio/${voice.file}`} type="audio/mpeg"></source>
        </audio>
        )
        }
    )
    
  

    
    return(
      <div >
        <h1>click</h1>
        {audio}
      </div>
    )
}
