import { useContext, useState } from 'react'
import {Guests} from './AllUsers'
import AuthContext from '../context/auth-context'
import {gql, useMutation} from '@apollo/client'
import { Redirect } from 'react-router'
import InitJoin from './InitJoinRoom'

const ADD_GUEST = gql`
mutation ($guestid:String,$roomid:String){
  addToRoom(guestid:$guestid,roomid:$roomid)
}
`

export default function Checkbeforjoining(param){
   
    const auth = useContext(AuthContext)
    let [data,Setdata] = useState(null) 
    let info ={
        room:param.roomid,
        userid:auth.userId
    }
     
    const [addUser] = useMutation(ADD_GUEST,{
        onCompleted:(data)=>{
           Setdata({data})
        },
        onError:(error)=>{
            console.log(error)
        },
    })

    addUser({variables:{guestid:info.userid,roomid:info.room}}).then(res=>{
        console.log(res)
    })
   
    if(data!=null || data != undefined){
    return(<Redirect to={`roomID=${info.room}`}/>)
    }
    else{
        return <Redirect to={`initJoinRoom`}/>
    }
}
