import { useMutation } from "@apollo/client";
import { Avatar, Button, Container, CssBaseline, Grid, makeStyles, TextField, Typography } from "@material-ui/core";
import { Lock, Mic } from "@material-ui/icons";
import gql from "graphql-tag";
import React, { useState } from "react";
import { Redirect } from "react-router";
import NavBar from "./Navbar";
import Navbar from './Navbar'

const useStyles = makeStyles((theme) => ({


    paper: {
      marginTop: theme.spacing(8),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
 
    form: {
      width: '100%', // Fix IE 11 issue.
      marginTop: theme.spacing(3),
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: "#000000",
        padding:'20px'
      },
    submit: {
      margin: theme.spacing(3, 0, 2),
     backgroundColor:theme.palette.secondary.main,
      '&:hover': {
          background: theme.palette.secondary.light,
        
       },
    },
  }));


  const CREATE_ROOM = gql`
mutation($roomname:String,$creator:String){
      createRoom(roomname:$roomname,creator:$creator){
       roomID
      }
   }
`
  

export default function CreateRoom(){
    const [state, setState] = useState({
        data:""
        });

    const [createRoom] = useMutation(CREATE_ROOM,{
        onCompleted:data=>{
            console.log(data)
            setState({roomID:data.createRoom.roomID})
        }
    })

    const roomnameRef = React.useRef()

    const handlesubmit=(e)=>{
        e.preventDefault()
        const roomname =roomnameRef.current.value
        const creator = "606b367f6a34b008e829d1f4"
        if(roomname.trim() === null){
            console.log("enetr the valid name")
        }
        else{
            createRoom({variables:{roomname,creator}})
            return(<Redirect to={`/roomID:${state.roomID}`}/>)
        }
    }

    const classes = useStyles();
    return(
        <div>
        <NavBar/>
        <Container component="main" maxWidth="xs">
           <CssBaseline />
           <div className={classes.paper}>
           <Avatar className={classes.avatar}>
          <Mic />
        </Avatar>
             <Typography component="h1" variant="h5">
               Let's Create Episode on Builder....
             </Typography>
             <form className={classes.form} onSubmit={handlesubmit}  noValidate>
               <Grid container spacing={2}>
     
                 <Grid item xs={12}>
                   <TextField
                     variant="outlined"
                     inputRef={roomnameRef}
                     required
                     fullWidth
                     id="roomname"
                     label="Room Name"
                     name="roomname"
                     autoComplete="off"
                   />
                 </Grid>
                
                 
                 
               </Grid>
               <Button
                 type="submit"
                 fullWidth
                 variant="contained"
                 color="primary"
                 className={classes.submit}
               >
                 create
               </Button>
              
             </form>
           </div>
          
         </Container>
         </div>
    )
}
