import {React,useState} from 'react'
import { userMutation, gql, useMutation } from '@apollo/client'
import { Redirect } from 'react-router'

export default function Userlogin(){


    const UPLOAD_USER = gql`
    mutation($username:String,$email:String,$password:String){
        addUser(username:$username,email:$email,password:$password){
          _id
          username
          email
          isGuest
        }
      }
`

const[createUser] = useMutation(UPLOAD_USER,{
    onCompleted: data => {
        console.log()
    }
})


let [state, setState] = useState({
    username:'',
    password:'',
    email:''
});

const handleUsername = (e)=>{
    console.log(state)
    setState({username:e.target.value,password:state.password,email:state.email})
}
const handlePassword = (e)=>{
    console.log(state)
    setState({username:state.username,password:e.target.value,email:state.email})
}
const handleEmail = (e)=>{
    console.log(state)
    setState({username:state.username,password:state.password,email:e.target.value})
}

const handleSubmit = (e)=>{
    e.preventDefault()
    createUser({ variables: { username:state.username,
        email:state.email,password:state.password } })
    return <Redirect to='/initJoinRoom'/>
}

    return(
        <form onSubmit={handleSubmit}>
            <input type="text" placeholder="guestname" name="username" value={state.username}
            onChange={handleUsername}/>
            <input type="password" placeholder="password" name="password" value={state.password}
            onChange={handlePassword}/>
            <input type="text" placeholder="email" name="email" value={state.email}
            onChange={handleEmail}/>
            <input type="submit"/>
        </form>
    )
}
