import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { InputLabel, NativeSelect } from '@material-ui/core';



const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
  },
  image: {
    margin:60,
    
  },
  img:{
    width:"70vh",
    hight:"70vh"
  },
  
  paper: {
    margin: theme.spacing(8, 4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
    button:{
    width:"100%",
    backgroundColor:"#01579b",
    color:"#ffffff",
    '&:hover': {
      background: "#006064",  
   }
  },

  form:{
    marginTop:20,
    width:"100%",
  },
  textfield:{
    width:"100%"
  },
  
}));

export default function SignInSide() {
  const classes = useStyles();

  const[contenttype,setContenttype]=React.useState('')
  const[country,setContry]=React.useState('')
  const[language,setLanguage]=React.useState('')
  const [state,setState] = React.useState({
    file:null,
    accepted:false
  })
  const handleChange = (event) => {

    switch(event.target.name){
      case 'contenttype':
        setContenttype({contenttype:event.target.value})
        break
      case 'country':
        setContry({country:event.target.value})
        break
      case 'contenttype':
        setLanguage({language:event.target.value})
        break
      case 'accepted':
        if(state.accepted) setState({accepted:false})
        else setState({accepted:true})
        break
      case 'file':
        setState({file:event.target.value})
        break
    }
    
  }

  const channelNameRef = React.useRef()
  const discriptionRef = React.useRef()

  const handleSubmit =(e)=>{
    e.preventDefault()
    const channelname = channelNameRef.current.value
    const discription = discriptionRef.current.value
    if(channelname.trim() != "" || discription.trim() != ""){
      console.log("form submitted")
    }
  }

  return (
    <Grid container component="main" className={classes.root}>
      <CssBaseline />
      <Grid item xs={false} sm={4} md={5}  >
        <div className={classes.image}>
          <img src={state.file} className={classes.img}/>
        </div>
        </Grid>
      <Grid item xs={12} sm={8} md={7} component={Paper} elevation={6} square>
        <div className={classes.paper}>
        <Typography component="h1" variant="h5">
          Create your channel
        </Typography>

        <form className={classes.form} onSubmit={handleSubmit}> 

        <Grid container spacing={2}>
          <Grid item xs={12}>
          <TextField className={classes.textfield} 
          id="outlined-basic" label="Channel Name" variant="outlined" />
          </Grid>

          <Grid item xs={12}>
          <TextField
          className={classes.textfield} 
          id="outlined-textarea"
          label="Discription About Channel"
          rows={4}
          multiline
          variant="outlined"
        />
          </Grid>

          <Grid item xs={4}>
          <InputLabel>Country</InputLabel>
          <NativeSelect
          id="demo-customized-select-native"
          // value={age}
          // onChange={handleChange}
         
        >
          <option aria-label="None" value="" />
          <option value={"India"}>India</option>
          
        </NativeSelect>
        
          </Grid>


          <Grid item xs={4}>
          <InputLabel>Language</InputLabel>
          <NativeSelect
          id="demo-customized-select-native"
          // value={age}
          // onChange={handleChange}
         
        >
          <option aria-label="None" value="" />
          <option value={"English"}>English</option>
          
        </NativeSelect>
        
          </Grid>

                    <Grid item xs={4}>
          <InputLabel>Content Type</InputLabel>
          <NativeSelect
          id="demo-customized-select-native"
          // value={age}
          name="contenttype"
          onChange={handleChange}
         
        >
          <option aria-label="None" value="" />
          <option value={"Education"}>Education</option>
          
        </NativeSelect>
        
          </Grid>

          <Grid item xs={12}>
          <Button variant="contained" component="label">Add Cover Image
            <input type="file" accept="image/png, image/jpeg" hidden 
            onChange={handleChange}></input>
            </Button>
          
          </Grid>

          <Grid item xs={12}>
          <FormControlLabel
            control={<Checkbox  onChange={handleChange} name="gilad" />}
            label="Accept Terms and Conditons"
          />
          </Grid>

          <Grid item xs={12}>
          <Button type="submit" className={classes.button}>Create My Channel</Button>
          </Grid>
          
          
        </Grid>

       

        </form>
       

        </div>
      </Grid>
    </Grid>
  );
}
