const { useQuery } = require('@apollo/react-hooks')
const gql = require("graphql-tag") 
const React = require('react')

export const fileQuery = gql`
    {
        files
    }
`;

export const File = ()=>{
    const { data,loading } = useQuery(filesQuery)
    if(loading){
        return <div>loading</div>
    }
    return(
        <div>
            {data.files.map(x=>(
                <img
                style={{width:200}}
                key = {x}
                src = {`http://localhost:4000/public/${x}`}
                alt={x}
                />
            ))}
        </div>
    )
}
