  const startRecording = () => {
    setState({ record: true });
  }
 
  const stopRecording = () => {
    setState({ record: false });
  }
  const { room } = useParams()

  const audioProcess=(file)=>{
    
    let speaker = auth.userId
    
    if(!file) return
