import React, { useEffect } from 'react'
import { useState } from 'react'

import {  gql, useLazyQuery, useQuery } from '@apollo/client'
// import Username from './Username'
import { useParams } from 'react-router'
import AllGuests from './AllGuests'

import { FormHelperText, makeStyles } from '@material-ui/core'
import { DragDropContext, Draggable, Droppable } from 'react-beautiful-dnd';

const SHOW_FILE = gql`
  query file ($roomid:String){
      files(roomid:$roomid){
        _id
        speaker
        file
        speech
      }
  }
`

// a887d8ae-3c6b-413f-a7fc-0ae0c8311dbe

const useStyles = makeStyles((theme) => ({
      box:{
        display:'block'
      },
      tab:{
        listStyleType:"none",
        borderRadius:"1px",
        backgroundColor:"#ede7f6",
        marginTop:1,
        marginLeft:5,
        fontWeight:"bold",
        fontSize:"15px",
        cursor:"pointer"
      },
      tabhead:{
      paddingLeft:"5px",
      paddingTop:"5px",
     
      },
      speech:{
        fontSize:"15px",
        fontWeight:"100",
      },

}))


function handleClick(id){
    console.log(id)
}

export default function EditorFiles(){

  

    const classes = useStyles();
    const [characters, updateCharacters] = useState([]);

    function handleOnDragEnd(result) {
      if (!result.destination) return;
  
      const items = Array.from(characters);
      const [reorderedItem] = items.splice(result.source.index, 1);
      items.splice(result.destination.index, 0, reorderedItem);
  
      updateCharacters(items);
    }
  

    const { room } = useParams()
    
    const{ loading,err,data} = useQuery(SHOW_FILE,{
      variables: {roomid:room},
      pollInterval: 500,
    })

  
    if(loading) return <p>loading</p>
   
    

   
 
      if(data === undefined || err ){
        characters = []
      }
      
      else{
    data.files.map((voice)=>{
        characters.push(voice)
        })
      }
     
    
    

    return(
      <div className={classes.box}>
          <DragDropContext>
              <Droppable droppableId="audioid">
                  {(provided)=>{
                      <ul {...provided.droppableProps} ref={provided.innerRef}>
                          {audio.map(({_id, speaker}, index)=>{
                              return (
                                <Draggable key={_id} draggableId={_id} index={index}>
                                  {(provided) => (
                                    <li ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                                      {/* <div className="characters-thumb">
                                        <img src={thumb} alt={`${name} Thumb`} />
                                      </div> */}
                                      <p>
                                        { speaker }
                                      </p>
                                    </li>
                                  )}
                                </Draggable>
                              )
                          })}
                      </ul>
                  }}
              </Droppable>
          </DragDropContext>
      </div>
    )
    
}
