import React, { useEffect } from 'react'
import { useState } from 'react'

import {  gql, useLazyQuery, useQuery } from '@apollo/client'
// import Username from './Username'
import { useParams } from 'react-router'
import AllGuests from './AllGuests'

import { FormHelperText, makeStyles } from '@material-ui/core'
import { DragDropContext, Draggable } from 'react-beautiful-dnd';

const SHOW_FILE = gql`
  query file ($roomid:String){
      files(roomid:$roomid){
        _id
        speaker
        file
        speech
      }
  }
`

// a887d8ae-3c6b-413f-a7fc-0ae0c8311dbe

const useStyles = makeStyles((theme) => ({
      box:{
        display:'block'
      },
      tab:{
        listStyleType:"none",
        borderRadius:"1px",
        backgroundColor:"#ede7f6",
        marginTop:1,
        marginLeft:5,
        fontWeight:"bold",
        fontSize:"15px",
        cursor:"pointer"
      },
      tabhead:{
      paddingLeft:"5px",
      paddingTop:"5px",
     
      },
      speech:{
        fontSize:"15px",
        fontWeight:"100",
      },

}))


function handleClick(id){
    console.log(id)
}

export default function EditorFiles(){

  

    const classes = useStyles();
    const [state,setState] = useState({
        listItem:[]
    })

    const { room } = useParams()
    
    const{ loading,err,data} = useQuery(SHOW_FILE,{
      variables: {roomid:room},
      pollInterval: 500,
    })

  
    if(loading) return <p>loading</p>
   
    

    let audio = []
 
      if(data === undefined || err ){
        audio = []
      }
      
      else{
    data.files.map((voice)=>{
        audio.push(
        <Draggable>
        <li key={voice._id} className={classes.tab} onClick={handleClick(voice._id)}>
          <div className={classes.tabhead}>
          {/* <AllGuests params={voice.speaker}/> */}
          user
          <p className={classes.speech}>{voice.speech}</p>
          </div>
          
        {/* <audio key={voice._id}  controls>
           <source  src={`http://localhost:4000/Audio/${voice.file}`} type="audio/mpeg"></source>
        </audio> */}
        </li>
        </Draggable>
        )
        }
       
    )
      }
    

    return(
      <div className={classes.box}>
        <DragDropContext>

        {audio}
        </DragDropContext>
      </div>
    )
    
}
