import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { Input, InputLabel, NativeSelect } from '@material-ui/core';



const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor:"#ffffff"
  },
  form:{
    marginTop:20,
    width:"100%",
  },
  textfield:{
    width:"100%"
  },
  button:{
    width:"100%",
    backgroundColor:"#01579b",
    color:"#ffffff",
    '&:hover': {
      background: "#006064",
     
   },
  }

}));

export default function SignUp() {
  const classes = useStyles();

  const [age, setAge] = React.useState('');
  const [state,setState] = React.useState({
    file:null
  })
  const handleChange = (event) => {
    // setAge(event.target.value);
    setState({
      file: URL.createObjectURL(event.target.files[0])
    })
  };

  return (
    <Container component="main" maxWidth="lg">
     
      <div className={classes.paper}>
        
        <Typography component="h1" variant="h5">
          Create your channel
        </Typography>

        <form className={classes.form}> 

        <Grid container spacing={1}>
          <Grid item lg={2}>
          <Grid item xs={12}>
          <TextField className={classes.textfield} 
          id="outlined-basic" label="Channel Name" variant="outlined" />
          </Grid>

          <Grid item xs={12}>
          <TextField
          className={classes.textfield} 
          id="outlined-textarea"
          label="Discription About Channel"
          rows={4}
          multiline
          variant="outlined"
        />
          </Grid>

          <Grid item xs={4}>
          <InputLabel>Country</InputLabel>
          <NativeSelect
          id="demo-customized-select-native"
          value={age}
          // onChange={handleChange}
         
        >
          <option aria-label="None" value="" />
          <option value={"India"}>India</option>
          
        </NativeSelect>
        
          </Grid>


          <Grid item xs={4}>
          <InputLabel>Language</InputLabel>
          <NativeSelect
          id="demo-customized-select-native"
          value={age}
          // onChange={handleChange}
         
        >
          <option aria-label="None" value="" />
          <option value={"English"}>English</option>
          
        </NativeSelect>
        
          </Grid>

                    <Grid item xs={4}>
          <InputLabel>Content Type</InputLabel>
          <NativeSelect
          id="demo-customized-select-native"
          value={age}
          // onChange={handleChange}
         
        >
          <option aria-label="None" value="" />
          <option value={"Education"}>Education</option>
          
        </NativeSelect>
        
          </Grid>

          <Grid item xs={12}>
          <Button variant="contained" component="label">Add Cover Image
            <input type="file" accept="image/png, image/jpeg" hidden 
            onChange={handleChange}></input>
            </Button>
            
          </Grid>

          <Grid item xs={12}>
          <FormControlLabel
            control={<Checkbox  onChange={handleChange} name="gilad" />}
            label="Accept Terms and Conditons"
          />
          </Grid>

          <Grid item xs={12}>
          <Button className={classes.button}>Create My Channel</Button>
          </Grid>
          </Grid>
          <Grid item lg={2}>
          <img src={state.file}/>
          </Grid>
          
        </Grid>

       

        </form>
       
      </div>

    </Container>
  );
}
