import React,{useState} from 'react'
import clsx from 'clsx';
import {Avatar, Button,Container,TextField, Typography, makeStyles} from '@material-ui/core';
import {  gql, useLazyQuery, useMutation } from '@apollo/client'
import NavBar from './Navbar';
import { Mic } from '@material-ui/icons';
import Checkbeforjoining from './Checkbeforjoining'
import AuthContext from '../context/auth-context'
import { useContext } from "react";
import { Redirect } from 'react-router'


const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor:"#000000",
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    backgroundColor: theme.palette.secondary.main,
    '&:hover': {
        background: theme.palette.secondary.light,
     },
  },
  container:{
    display:"flex",
    marginTop:100
  },
  sidepanel:{
   backgroundColor:"#fafafa"
  },
  list:{
   
    marginTop:10,
    padding:10,
    listStyleType:"none",
    borderRadius:5
  },
  orange:{
    backgroundColor:"#ffccbc"
  },
  yellow:{
    backgroundColor:"#fff9c4"
  },
  blue:{
    backgroundColor:"#b3e5fc"
  }
}));


    const FIND_ROOM = gql`
    query findRoom($id:String){
        findRoom(id:$id){
          roomID
          roomname
          creator
          guestList
        }
      }
`

export default function InitJoinRoom() {
  const classes = useStyles();

  const auth = useContext(AuthContext)
  let classNameHolder = [classes.orange,classes.blue,classes.yellow]
  const [state,setstate] = useState({
      _isRoomFound:false,
      room:"",
      _isSubmitted:false
  })
  const [isSubmit,setSubmit]=useState(false)
  const [isFound,setFound]=useState(false)
  const roomIDRef = React.useRef()

  const [findroom ,{loading,data}] = useLazyQuery(FIND_ROOM,{
        variables: {id:state.room}
      })
      if(loading ) {return (<p>loading</p>)}
      let sendAuth={
          id:"",
          room:""
        }
   
    const handleSubmit =(e)=>{
        e.preventDefault()
        setstate({room:roomIDRef.current.value})
        findroom()
        
        if(data){
          console.log(data)
          // setstate({_isRoomFound:true})
          // const tempAuth = auth.userId.toString()
          if(!data.findRoom.guestList.includes(auth.userId)){
            sendAuth={
              id:auth.userId,
              room:state.room
            }
          }
          else{
            
            setSubmit(true)
            console.log(isSubmit)
          }

          setFound(true)
          console.log(isFound)
        }
        else{
          setFound(false)
        }
    }
    

      if(!isFound){
      return (
   
    <div>
    <NavBar/>
    <div className={classes.container}>
      <Container maxWidth="xs">
     
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <Mic />
        </Avatar>
        <Typography component="h1" variant="h5">
          Room ID
        </Typography>
        <form className={classes.form} onSubmit={handleSubmit} noValidate>
        
          <TextField
            variant="outlined"
            margin="normal"
            inputRef={roomIDRef}
            required
            fullWidth
            name="roomid"
            label="RoomId"
            type="text"
            id="roomid"
            
          />
          
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Join
          </Button>
          
        </form>
      </div>
      </Container>
      {/* <Box mt={8}>
        <Copyright />
      </Box> */}

<div className={classes.sidepanel}>
        <Container maxWidth="xs">
          <h4>Created Rooms</h4>
          <ul>
          <li className={clsx(classes.list, classNameHolder[Math.floor(Math.random()*3)])}>aee04343-1c85-41cb-b375-2493a8efa2b0</li>
          <li className={clsx(classes.list, classNameHolder[Math.floor(Math.random()*3)])}>aee04343-1c85-41cb-b375-2493a8efa2b0</li>
          <li className={clsx(classes.list, classNameHolder[Math.floor(Math.random()*3)])}>aee04343-1c85-41cb-b375-2493a8efa2b0</li>
          <li className={clsx(classes.list, classNameHolder[Math.floor(Math.random()*3)])}>aee04343-1c85-41cb-b375-2493a8efa2b0</li>
          </ul>
        </Container>
    </div>
    </div>
    
</div>
  )
    }
     if(isSubmit && isFound){
      return(<Redirect to={`roomID=${state.room}`}/>)
    }
    if(!isSubmit && isFound){
      return(<AddGuest param={state.room}/>)
    }
}

const ADD_GUEST = gql`
mutation ($guestid:String,$roomid:String){
  addToRoom(guestid:$guestid,roomid:$roomid)
}
`

function AddGuest(param){
  console.log(param)
  const auth = useContext(AuthContext)
   const [addUser,{loading,data}] = useMutation(ADD_GUEST,{
        onCompleted:(data)=>{
            console.log(data)
        },
        onError:(error)=>{
            console.log(error)
        },
    })

   addUser({variables:{guestid:auth.userId,roomid:param.room}})

  return(<Redirect to={`roomID=${param.param}`}/>)
}
